// Example pre-rendering data
// src/_data/eleventyComputed.js
import markdownit from "markdown-it";
const md = markdownit();

export default {
    renderedPosts: (data) => {
        const posts = data.posts.map(post => {
            post.content = md.render(post.content);
            return post;
        })
        return posts;
    }
}
