function enableEnterOnButtonLabels () {
    const buttonLabels = document.querySelectorAll("label[role='button']")
    for (let i = 0; i < buttonLabels.length; i++) {
        buttonLabels[i].addEventListener("keyup", (e) => {
            if (e.code == "Enter") {
                e.target.click();
            }
        });
    }
}
